import React from 'react';
import './App.css';
import './index.css';
import { useState, useEffect } from "react";
import axios from "axios";
import { Task } from "./types";

const API_URL = "http://localhost:5000/api/tasks";

const App: React.FC = () => {
  const [tasks, setTasks] = useState<Task[]>([]);
  const [desc, setDesc] = useState("");

  useEffect(() => {
    axios.get(API_URL).then(res => setTasks(res.data));
  }, []);

  useEffect(() => {
    localStorage.setItem("tasks", JSON.stringify(tasks));
  }, [tasks]);

  const addTask = async () => {
    if (!desc) return;
    const res = await axios.post(API_URL, { description: desc });
    setTasks([...tasks, res.data]);
    setDesc("");
  };

  const toggleTask = async (id: number) => {
    const res = await axios.put(`${API_URL}/${id}/toggle`);
    setTasks(tasks.map(t => (t.id === id ? res.data : t)));
  };

  const deleteTask = async (id: number) => {
    await axios.delete(`${API_URL}/${id}`);
    setTasks(tasks.filter(t => t.id !== id));
  };

  return (
    <div className="max-w-xl mx-auto mt-8 p-4 bg-white shadow-md rounded">
  <h2 className="text-2xl font-bold mb-4 text-center">Task Manager</h2>

  <div className="flex mb-4 gap-2">
    <input
      type="text"
      value={desc}
      onChange={e => setDesc(e.target.value)}
      className="flex-1 border border-gray-300 rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-400"
      placeholder="Enter a task"
    />
    <button
      onClick={addTask}
      className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 transition-colors"
    >
      Add
    </button>
  </div>

  <ul className="divide-y divide-gray-200">
    {tasks.map(t => (
      <li
        key={t.id}
        className="flex justify-between items-center py-2"
      >
        <span
          className={`flex-1 cursor-pointer ${
            t.isCompleted ? "line-through text-gray-400" : "text-gray-800"
          }`}
          onClick={() => toggleTask(t.id)}
        >
          {t.description}
        </span>
        <button
          onClick={() => deleteTask(t.id)}
          className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600 transition-colors"
        >
          X
        </button>
      </li>
    ))}
  </ul>
</div>

  );
};

export default App;
